using System.Collections;using System.Collections.Generic;using UnityEngine;[RequireComponent(typeof(EdgeCollider2D))]public class PolynomialCollider2D : MonoBehaviour {    public int degree = 2;    public float[] coefficients;    public float left = -1f;    public float right = 1f;    public float sampleStep = 0.05f;    private float Polynomial(float x) {        float result = 0;        float coe = 0f;        for (int i=degree-1; i>=0; i--) {            if (i > coefficients.Length) {                coe = 0f;            } else {                coe = coefficients[i];            }            result = result * x + coe;        }        return result;    }    public Vector2[] EdgePoints() {        int count = (int)((right - left) / sampleStep);        float x = left;        Vector2[] points = new Vector2[count + 1];        for (int i=0; i<count; i++) {            points[i] = new Vector2(x, Polynomial(x));            x += sampleStep;        }        points[count] = new Vector2(right, Polynomial(right));        return points;    }}