using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;[CustomEditor(typeof(PolynomialCollider2D))][CanEditMultipleObjects]public class PolynomialCollider2DEditor : Editor {    private SerializedProperty degree;    private SerializedProperty coefficients;    private SerializedProperty left;    private SerializedProperty right;    private SerializedProperty sampleStep;    private PolynomialCollider2D polyCollider;    private EdgeCollider2D edgeCollider;    void OnEnable() {        polyCollider = (PolynomialCollider2D)target;        edgeCollider = polyCollider.GetComponent<EdgeCollider2D>();        degree = serializedObject.FindProperty("degree");        coefficients = serializedObject.FindProperty("coefficients");        left = serializedObject.FindProperty("left");        right = serializedObject.FindProperty("right");        sampleStep = serializedObject.FindProperty("sampleStep");    }    public override void OnInspectorGUI() {        EditorGUILayout.PropertyField(degree);        EditorGUILayout.PropertyField(coefficients, true);        EditorGUILayout.PropertyField(left);        EditorGUILayout.PropertyField(right);        EditorGUILayout.PropertyField(sampleStep);        serializedObject.ApplyModifiedProperties();        if (edgeCollider) {            edgeCollider.points = polyCollider.EdgePoints();        }    }}